<article typeof="sioc:UserAccount" class="card contained user-profile view-mode-grid clearfix">
	<a href="__USER_URL__">
		<div class="media">
			<div class="cover">
				<img typeof="foaf:Image" id="cover-user-__UID__" src="data:image/gif;base64," data-src="__COVER_IMAGE_URL__" width="400" height="225" alt="" onLoad='var fullimage = document.getElementById("cover-user-__UID__");if (fullimage.getAttribute("data-src") != "") {var preload_fullimage = new Image();preload_fullimage.src = fullimage.getAttribute("data-src");preload_fullimage.onload = function() {fullimage.setAttribute("src",fullimage.getAttribute("data-src"));fullimage.setAttribute("data-src", "");}}'>
			</div>
			<div class="avatar">
				<span class="badge-user">
					<img typeof="foaf:Image" src="__AVATAR_URL__" width="100" height="100" alt="">
				</span>
			</div>
		</div>
		<div class="teaser">
			<h1 class="prime-title">__FULL_NAME__</h1>
			<p class="meta">
				__JOB_ROLE__
				__ORGANIZATION_NAME__
			</p>
		</div>
	</a>
	<div class="extra">
		<a class="chapter-sticker" href="__CHAPTER_URL__" style="background-color: __CHAPTER_COLOR__">__CHAPTER_CODE__</a>
	</div>
</article>



